CREATE OR REPLACE FUNCTION ageint_obter_item_agendamento (ie_tipo_agendamento_p text, nr_seq_agendamento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_ageint_item_w	bigint;
			

BEGIN

IF (ie_tipo_agendamento_p IN ('C','S')) THEN

select max(nr_sequencia)
  into STRICT nr_seq_ageint_item_w
  from agenda_integrada_item
  WHERE NR_SEQ_AGENDA_CONS = nr_seq_agendamento_p;

ELSIF (ie_tipo_agendamento_p = 'Q') THEN

select max(nr_sequencia) 
  into STRICT nr_seq_ageint_item_w
  from agenda_integrada_item
  WHERE NR_SEQ_AGEQUI = nr_seq_agendamento_p;

ELSIF (ie_tipo_agendamento_p = 'E') THEN

select max(nr_sequencia)
  into STRICT nr_seq_ageint_item_w
  from agenda_integrada_item
  WHERE NR_SEQ_AGENDA_EXAME = nr_seq_agendamento_p;

END IF;

return	nr_seq_ageint_item_w;

end;
