SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ageint_obter_lista_email (nr_seq_ageint_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_email_destino_w	varchar(255);
cd_medico_w		varchar(10);
qt_regra_w		bigint;
ds_retorno_w		varchar(4000);
ie_tipo_compl_corresp_w	smallint;

C01 CURSOR FOR
	SELECT	distinct b.cd_medico_req
	from	agenda_integrada_item a,
		agenda_consulta b
	where	a.nr_seq_agenda_cons = b.nr_sequencia
	and	a.nr_seq_agenda_int = nr_seq_ageint_p;


BEGIN

open C01;
loop
fetch C01 into
	cd_medico_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_regra_w
	from	ageint_texto_padrao_proc
	where	cd_pessoa_fisica = cd_medico_w
	and	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
	and	ie_tipo_agenda = 'C';

	if (qt_regra_w > 0) then

		select	max(ie_tipo_compl_corresp)
		into STRICT	ie_tipo_compl_corresp_w
		from	medico
		where	cd_pessoa_fisica = cd_medico_w;

		select	max(b.ds_email)
		into STRICT	ds_email_destino_w
		from	pessoa_fisica a,
			compl_pessoa_fisica b
		where	a.cd_pessoa_fisica 	= b.cd_pessoa_fisica
		and	b.ie_tipo_complemento 	= ie_tipo_compl_corresp_w
		and	a.cd_pessoa_fisica	= cd_medico_w;

		ds_retorno_w	:= ds_retorno_w || ds_email_destino_w ||', ';
	end if;

	end;
end loop;
close C01;

ds_retorno_w	:= substr(ds_retorno_w,1,length(ds_retorno_w)-2);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
