CREATE OR REPLACE FUNCTION ageint_obter_medico_item ( nr_seq_agenda_cons_p bigint, nr_seq_agenda_exame_p bigint, nr_seq_agenda_int_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_Retorno_w	varchar(60);


BEGIN

if (nr_seq_Agenda_cons_p IS NOT NULL AND nr_seq_Agenda_cons_p::text <> '') then
	select	max(coalesce(substr(obter_nome_medico(a.cd_medico_req,'NCO'),1,60),
      SUBSTR(OBTER_NOME_PF(b.cd_pessoa_fisica), 0, 60)
    ))
	into STRICT	ds_Retorno_w
	FROM agenda_consulta a
LEFT OUTER JOIN pessoa_fisica b ON (a.cd_medico = b.cd_pessoa_fisica)
WHERE a.nr_sequencia	= nr_seq_Agenda_cons_p;
elsif (nr_seq_Agenda_exame_p IS NOT NULL AND nr_seq_Agenda_exame_p::text <> '') then
	select	max(SUBSTR(OBTER_NOME_PF(b.cd_pessoa_fisica), 0, 60))
	into STRICT	ds_Retorno_w
	from	agenda_paciente a,
		pessoa_fisica b
	where	a.nr_sequencia		= nr_seq_Agenda_exame_p
	and	a.cd_medico_Exec	= b.cd_pessoa_fisica;
elsif (nr_seq_agenda_int_p IS NOT NULL AND nr_seq_agenda_int_p::text <> '') then
	begin
	select	max(SUBSTR(OBTER_NOME_PF(b.cd_pessoa_fisica), 0, 60))
	into STRICT	ds_Retorno_w
	from	agenda_integrada_prof_item a,
			pessoa_fisica b
	where	a.nr_seq_agenda_item = nr_seq_agenda_int_p
	and		a.cd_pessoa_fisica	= b.cd_pessoa_fisica;
	end;
else
	ds_retorno_w	:= '';
end if;

return	ds_Retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
