CREATE OR REPLACE FUNCTION ageint_obter_nome_pessoa (NM_USUARIO_P text) RETURNS varchar AS $body$
DECLARE

         
NM_COMPLETO_W varchar(100);
NM_PESSOA_FISICA_W PESSOA_FISICA.NM_PESSOA_FISICA%TYPE;


BEGIN
  IF (NM_USUARIO_P IS NOT NULL AND NM_USUARIO_P::text <> '') THEN
  BEGIN
  SELECT DS_USUARIO,
         OBTER_NOME_PF(CD_PESSOA_FISICA)
  INTO STRICT   NM_COMPLETO_W,
         NM_PESSOA_FISICA_W
  FROM   USUARIO
  WHERE  NM_USUARIO = NM_USUARIO_P;

  EXCEPTION
  WHEN OTHERS THEN
    NM_COMPLETO_W := '';
  END;
  END IF;

  RETURN coalesce(NM_PESSOA_FISICA_W, NM_COMPLETO_W);
END;
$body$
LANGUAGE PLPGSQL
