CREATE OR REPLACE FUNCTION ageint_obter_observacao_final ( nr_seq_agenda_cons_p bigint, nr_seq_agenda_exame_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (nr_seq_agenda_cons_p IS NOT NULL AND nr_seq_agenda_cons_p::text <> '') then
	select	distinct
		substr(max(b.ds_observacao),1,255) ds_observacao
	into STRICT	ds_retorno_w
	from	ageint_observacao_final b,
		agenda_integrada_item c
	where	c.nr_seq_agenda_int 	= b.nr_seq_ageint
	and	c.nr_seq_agenda_cons 	= nr_seq_agenda_cons_p;
elsif (nr_seq_agenda_exame_p IS NOT NULL AND nr_seq_agenda_exame_p::text <> '') then
	select	distinct
		substr(max(b.ds_observacao),1,255) ds_observacao
	into STRICT	ds_retorno_w
	from	ageint_observacao_final b,
		agenda_integrada_item c
	where	c.nr_seq_agenda_int 	= b.nr_seq_ageint
	and	c.nr_seq_agenda_exame 	= nr_seq_agenda_exame_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
