CREATE OR REPLACE FUNCTION ageint_obter_orient_anest ( cd_agenda_p bigint, dt_agenda_p timestamp, nr_seq_ageint_p bigint, nr_seq_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000)	:= '';
dt_dia_semana_w		bigint;
ie_anestesia_w	varchar(1);


BEGIN

if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') and (nr_seq_ageint_p IS NOT NULL AND nr_seq_ageint_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then

	select	max(coalesce(ie_anestesia,'N'))
	into STRICT	ie_anestesia_w
	from	agenda_integrada_item
	where	nr_seq_agenda_int = nr_seq_ageint_p
	and	nr_sequencia = nr_seq_item_p;

	dt_dia_semana_w	:= obter_cod_dia_semana(dt_agenda_p);

	if (ie_anestesia_w = 'S') then
		select	substr(max(ds_orientacao),1,4000)
		into STRICT	ds_retorno_w
		from	ageint_anestesia_turno a,
			ageint_agenda_turno_anest b
		where	b.nr_seq_anest_turno	= a.nr_sequencia
		and	b.cd_agenda	= cd_agenda_p
		and 	coalesce(a.cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento
		and	((a.dt_inicial_vigencia > dt_agenda_p) or (coalesce(a.dt_inicial_vigencia::text, '') = ''))
		and	((a.dt_final_vigencia > dt_agenda_p) or (coalesce(a.dt_final_vigencia::text, '') = ''))
		and	((a.dt_dia_semana	= dt_dia_semana_w) or (a.dt_dia_semana = 9 and dt_dia_semana_w not in (1,7)))
		and	dt_agenda_p between to_date(to_char(dt_agenda_p,'dd/mm/yyyy') || ' ' || to_char(a.hr_inicial,'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss')
			and to_date(to_char(dt_agenda_p,'dd/mm/yyyy') || ' ' || to_char(a.hr_final,'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss');
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
