CREATE OR REPLACE FUNCTION ageint_obter_regra_perm_bloq ( cd_pessoa_fisica_p text, cd_perfil_p bigint, cd_Estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(15)	:= 'N';
qt_regra_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	ageint_regra_perm_bloq
where	coalesce(ie_situacao, 'A')	= 'A'
and		coalesce(cd_Estabelecimento, cd_estabelecimento_p)	= cd_estabelecimento_p;

if (qt_regra_w	> 0) then
	select	coalesce(max(ie_forma_perm),'N')
	into STRICT	ds_retorno_w
	from	ageint_regra_perm_bloq
	where	coalesce(cd_perfil, cd_perfil_p)			= cd_perfil_p
	and		((coalesce(cd_pessoa_fisica, cd_pessoa_fisica_p)	= cd_pessoa_fisica_p) or (coalesce(cd_pessoa_fisica::text, '') = ''))
	and		coalesce(ie_situacao, 'A')				= 'A'
	and		coalesce(cd_Estabelecimento, cd_estabelecimento_p)	= cd_estabelecimento_p;
end if;

return	ds_Retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
