CREATE OR REPLACE FUNCTION ageint_obter_rerverva_pac ( nr_seq_agenda_p bigint, dt_agenda_p timestamp, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(20);			
ds_nome_abrev_w		varchar(50);
nr_seq_reserva_w		bigint;
			

BEGIN 
 
select	max(b.nr_reserva) 
into STRICT	ds_retorno_w 
from	agenda a, 
	agenda_paciente b 
where	a.cd_agenda		= b.cd_agenda 
and	a.cd_tipo_agenda	= 2 
and	b.hr_inicio		> dt_agenda_p 
and	b.cd_pessoa_fisica	= cd_pessoa_Fisica_p 
and	b.nr_sequencia		<> nr_seq_agenda_p 
and	b.ie_status_agenda not in ('C','L');
 
if (coalesce(ds_Retorno_w::text, '') = '') then 
	ds_nome_abrev_w := upper(obter_abrev_estabelecimento(cd_estabelecimento_p));
	 
	select	nextval('agenda_reserva_seq') 
	into STRICT	nr_seq_reserva_w 
	;
	 
	ds_retorno_w := substr(ds_nome_abrev_w||'E'||nr_seq_reserva_w,1,20);
end if;
 
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
