CREATE OR REPLACE FUNCTION ageint_obter_resp_autor_regra ( nr_seq_regra_p bigint) RETURNS varchar AS $body$
DECLARE


ie_resp_autor_w	varchar(3);
ds_retorno_w	varchar(80);


BEGIN

select	max(ie_resp_autor)
into STRICT	ie_resp_autor_w
from	regra_convenio_plano
where	nr_sequencia	= nr_seq_regra_p;

if (ie_resp_autor_w IS NOT NULL AND ie_resp_autor_w::text <> '') then
	ds_retorno_w	:= substr(obter_valor_dominio(1904, ie_resp_autor_w),1,80);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
