CREATE OR REPLACE FUNCTION ageint_obter_se_agendado ( cd_pessoa_fisica_p text, nr_seq_ageint_p bigint, nr_seq_item_p bigint, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
cd_procedimento_w	bigint;
nr_seq_proc_interno_w	bigint;
ie_origem_proced_w	bigint;
cd_area_procedimento_w	bigint;
cd_especialidade_w	bigint;
cd_grupo_proc_w		bigint;
ie_perm_agendar_w	varchar(1);


BEGIN

select	coalesce(max(cd_procedimento),0),
	coalesce(max(nr_seq_proc_interno),0),
	coalesce(max(ie_origem_proced),0)
into STRICT	cd_procedimento_w,
	nr_seq_proc_interno_w,
	ie_origem_proced_w
from	agenda_integrada_item
where	nr_sequencia	= nr_seq_item_p;

if (nr_seq_proc_interno_w	> 0) then
	ie_perm_agendar_w	:= Ageint_Perm_Marcar_Agendado(cd_procedimento_w, nr_seq_proc_interno_w, ie_origem_proced_w);

	if (ie_perm_agendar_w = 'N') then
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	agenda_paciente a
		where	a.nr_seq_proc_interno	= nr_seq_proc_interno_w
		and	a.ie_origem_proced	= ie_origem_proced_w
		and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	a.ie_Status_agenda not in ('L','C')
		and	to_date(to_char(dt_agenda,'dd/mm/yyyy') ||' '|| to_char(hr_inicio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') > clock_timestamp()
		and	not exists (	SELECT 1
					from agenda_integrada_item b
					where b.nr_seq_agenda_int = nr_seq_ageint_p
					and b.nr_seq_agenda_exame = a.nr_sequencia);
					--and b.nr_sequencia = nr_seq_item_p);
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
