SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ageint_obter_se_canc_reag ( nr_seq_ageint_p bigint, dt_inicio_p timestamp, dt_final_p timestamp, ie_tipo_p text ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'S';

BEGIN

if (coalesce(nr_seq_ageint_p,0) > 0) and (ie_tipo_p IS NOT NULL AND ie_tipo_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from 	agenda_integrada a,
		agenda_integrada_item b,
		agenda_paciente c
	where	a.nr_sequencia	   = nr_seq_ageint_p
	and	c.nr_sequencia 	   = b.nr_seq_agenda_exame
	and	a.nr_sequencia 	   = b.nr_seq_agenda_int
	--and	c.ie_status_agenda = 'N'
	--and	trunc(c.hr_inicio) between trunc(dt_inicio_p) and trunc(dt_final_p)
	--and	c.dt_confirmacao   is null
	and	(((coalesce(c.ie_status_agenda,'X') =  'C' or   coalesce(c.ie_transferido,'X')  = 'S') and ie_tipo_p = 'A') or
		 ((coalesce(c.ie_status_agenda,'X') <> 'C' and  coalesce(c.ie_transferido,'X') <> 'S') and ie_tipo_p = 'N') or (coalesce(c.ie_status_agenda,'X') =  'C' and ie_tipo_p = 'C') or (coalesce(c.ie_transferido,'X')   =  'S' and ie_tipo_p = 'R') or (ie_tipo_p = 'X')
		)
	and	not exists (	SELECT 	1
			from 	agenda_integrada x,
				agenda_integrada_item y,
				agenda_paciente z
			where	x.nr_sequencia	   = nr_seq_ageint_p
			and	z.nr_sequencia 	   = y.nr_seq_agenda_exame
			and	x.nr_sequencia 	   = y.nr_seq_agenda_int
			and	z.ie_transferido = 'S'
			and	ie_tipo_p = 'N'
			       );

	if (coalesce(ds_retorno_w,'N') = 'N') then

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from 	agenda_integrada a,
			agenda_integrada_item b,
			agenda_consulta c
		where	a.nr_sequencia	   = nr_seq_ageint_p
		and	c.nr_sequencia 	   = b.nr_seq_agenda_cons
		and	a.nr_sequencia 	   = b.nr_seq_agenda_int
		--and	c.ie_status_agenda = 'N'
		--and	trunc(c.dt_agenda) between trunc(dt_inicio_p) and trunc(dt_final_p)
		--and	c.dt_confirmacao   is null
		and	(((coalesce(c.ie_status_agenda,'X') =  'C' or   coalesce(c.ie_transferido,'X')  = 'S') and ie_tipo_p = 'A') or
			 ((coalesce(c.ie_status_agenda,'X') <> 'C' and  coalesce(c.ie_transferido,'X') <> 'S') and ie_tipo_p = 'N') or (coalesce(c.ie_status_agenda,'X') =  'C' and ie_tipo_p = 'C') or (coalesce(c.ie_transferido,'X')   =  'S' and ie_tipo_p = 'R') or (ie_tipo_p = 'X')
			)
		and	not exists (	SELECT 	1
				from 	agenda_integrada x,
					agenda_integrada_item y,
					agenda_consulta z
				where	x.nr_sequencia	   = nr_seq_ageint_p
				and	z.nr_sequencia 	   = y.nr_seq_agenda_cons
				and	x.nr_sequencia 	   = y.nr_seq_agenda_int
				and	z.ie_transferido = 'S'
				and	ie_tipo_p = 'N'
			       );
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
