SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ageint_obter_se_classif_grupo (ie_classif_agenda_p text, nr_seq_item_grupo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(2) := 'S';
qt_regra_classif_w	bigint;			
cd_medico_w		agenda_int_grupo_item.cd_medico%type;
cd_especialidade_w	agenda_int_grupo_item.cd_especialidade%type;
ie_especialidade_adic_w varchar(1);


BEGIN

ie_especialidade_adic_w := obter_param_usuario(869, 448, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_especialidade_adic_w);

select	max(cd_especialidade),
	max(cd_medico)
into STRICT	cd_especialidade_w,
	cd_medico_w
from	agenda_int_grupo_item
where	nr_sequencia = nr_seq_item_grupo_p;

select	count(*)
into STRICT	qt_regra_classif_w
from	regra_classif_agenda a,
	agenda b
where	a.cd_agenda = b.cd_agenda
and	b.cd_tipo_agenda = 3
and	((b.cd_pessoa_fisica = cd_medico_w) or (coalesce(cd_medico_w::text, '') = ''))
and ((b.cd_especialidade = cd_especialidade_w) or (exists (SELECT cd_especialidade 
														   from agenda_cons_especialidade 
														   where cd_especialidade = cd_especialidade_w) and ie_especialidade_adic_w = 'S'))
and	a.ie_situacao = 'A';

if (qt_regra_classif_w > 0) then
	select	count(*)
	into STRICT	qt_regra_classif_w
	from	regra_classif_agenda a,
		    agenda b
	where	a.cd_agenda = b.cd_agenda
	and	b.cd_tipo_agenda = 3
	and	((b.cd_pessoa_fisica = cd_medico_w) or (coalesce(cd_medico_w::text, '') = ''))
	and ((b.cd_especialidade = cd_especialidade_w) or (exists (SELECT cd_especialidade 
															   from agenda_cons_especialidade 
															   where cd_especialidade = cd_especialidade_w) and ie_especialidade_adic_w = 'S'))
	and	a.ie_situacao = 'A'
	and	a.ie_classif_agenda = ie_classif_agenda_p;
	if (qt_regra_classif_w = 0) then
		ie_retorno_w := 'N';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
