CREATE OR REPLACE FUNCTION ageint_obter_se_hor_bloq (cd_agenda_p bigint, dt_agenda_p timestamp, cd_convenio_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE


ie_dia_semana_w		smallint;
ie_bloqueio_w		varchar(1);
ie_bloq_med_w		varchar(1);
ie_retorno_w		varchar(1) := 'N';


BEGIN

ie_dia_semana_w	:= obter_cod_dia_semana(dt_agenda_p);

