ie_bloq_med_w	:= Ageint_Obter_Se_Hor_Bloq_Med(cd_medico_p, cd_agenda_p, dt_agenda_p, cd_convenio_p);

if (ie_bloqueio_w = 'S') or (ie_bloq_med_w = 'S') then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
