CREATE OR REPLACE FUNCTION ageint_obter_se_item_encaixe ( nr_seq_agenda_exame_p bigint, nr_seq_agenda_cons_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10);
ie_sim_w			varchar(10) := wheb_mensagem_pck.get_texto(306949, null);
ie_nao_w			varchar(10) := wheb_mensagem_pck.get_texto(306950, null);

BEGIN
ds_retorno_w	:= ie_nao_w;

if (coalesce(nr_seq_agenda_exame_p,0) > 0) then

	select	CASE WHEN coalesce(max(ie_encaixe),'N')='S' THEN ie_sim_w  ELSE ie_nao_w END
	into STRICT	ds_retorno_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_agenda_exame_p;

elsif (coalesce(nr_seq_agenda_cons_p,0)  > 0) then

	select	CASE WHEN coalesce(max(ie_encaixe),'N')='S' THEN ie_sim_w  ELSE ie_nao_w END
	into STRICT	ds_retorno_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_cons_p;

end if;

return	ds_retorno_w;

end;
