CREATE OR REPLACE FUNCTION ageint_obter_se_item_glosa (nr_seq_ageint_item_p bigint) RETURNS varchar AS $body$
DECLARE


ie_bloq_glosa_part_w	varchar(1);
nr_glosa_part_w		bigint;
ie_retorno_w		varchar(10);


BEGIN

ie_bloq_glosa_part_w := obter_param_usuario(869, 187, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_bloq_glosa_part_w);

nr_glosa_part_w	:= 99;

if (ie_bloq_glosa_part_w = 'N') then
	nr_glosa_part_w := 8;
end if;

begin
select	wheb_mensagem_pck.get_texto(306949, null) --  SIM
into STRICT	ie_retorno_w
from	ageint_exame_adic_item a
where	a.nr_seq_item = nr_seq_ageint_item_p
and	((coalesce(a.ie_regra,0) in (1,2,5,nr_glosa_part_w)) or (coalesce(a.ie_glosa,'X') = 'T'))
and	not exists(	SELECT	1
			from	ageint_exame_adic_item x
			where	a.nr_sequencia = x.nr_seq_origem
			and	((coalesce(x.ie_regra,0) not in (1,2,5,nr_glosa_part_w)) or (coalesce(x.ie_glosa,'X') <> 'T')))  LIMIT 1;
exception
when no_data_found then
	ie_retorno_w	:= wheb_mensagem_pck.get_texto(306950, null); -- NÃ£o
end;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
