CREATE OR REPLACE FUNCTION ageint_obter_se_itens_pend ( nm_usuario_p text) RETURNS varchar AS $body$
DECLARE



qt_marc_pendente_w	bigint	:= 0;
qt_marc_Pend_ageint_w	bigint;
cd_profissional_w	varchar(10);
nr_Seq_Ageint_w		bigint;
ds_retorno_W		varchar(1)	:= 'N';


BEGIN

select 	max(cd_pessoa_fisica)
into STRICT	cd_profissional_W
from	usuario
where	nm_usuario	= nm_usuario_p;


select	count(*)
into STRICT	qt_marc_pendente_w
from	agenda_integrada a,
	agenda_integrada_item b
where	a.nr_sequencia	= b.nr_seq_agenda_int
and	a.dt_inicio_Agendamento between trunc(clock_timestamp()) and trunc(clock_timestamp()) +  + 86399/86400
and	a.cd_profissional	= cd_profissional_w
and	coalesce(b.nr_seq_agenda_exame::text, '') = ''
and	coalesce(b.nr_seq_Agenda_cons::text, '') = '';

if (qt_marc_pendente_w	> 0) then
	ds_retorno_W	:= 'S';
end if;

return	ds_retorno_W;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
