CREATE OR REPLACE FUNCTION ageint_obter_se_pacote ( cd_convenio_p bigint, cd_estabelecimento_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(3)	:= 'N';
qt_pacote_w		smallint;


BEGIN
begin
select	1
into STRICT	qt_pacote_w
from	pacote a,
		pacote_tipo_acomodacao b
where   a.cd_convenio     = cd_convenio_p
and     a.ie_situacao     = 'A'
and     a.cd_estabelecimento = cd_estabelecimento_p
and	   	exists (SELECT 1 FROM tipo_acomodacao x
LEFT OUTER JOIN ageint_tipo_acomod_padrao y ON (x.cd_tipo_acomodacao = y.cd_tipo_acomodacao)
WHERE x.ie_classificacao = b.ie_tipo_acomod )
and		a.nr_seq_pacote = b.nr_seq_pacote
and 	coalesce(b.dt_vigencia,clock_timestamp()) = (select 	max(coalesce(c.dt_vigencia, clock_timestamp()))
											from 	pacote_tipo_acomodacao c
											where    	c.nr_seq_pacote = a.nr_seq_pacote)  LIMIT 1;
exception
	when others then
	qt_pacote_w	:= 0;
end;
if (qt_pacote_w	> 0) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
