CREATE OR REPLACE FUNCTION ageint_obter_se_proc_anest (nr_seq_proc_interno_p bigint, cd_procedimento_p bigint, ie_origem_proc_p bigint, nr_seq_ageint_p bigint) RETURNS varchar AS $body$
DECLARE


ie_proc_anest_w	varchar(1) := 'N';
qt_idade_w		bigint;
cd_pessoa_Fisica_w	varchar(10);
ie_dependente_w		varchar(1);


BEGIN

if (nr_seq_proc_interno_p > 0) then

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_proc_anest_w
	from 	proc_interno
	where 	nr_sequencia = nr_seq_proc_interno_p
	and	coalesce(ie_necessita_anest,'N') = 'S';

elsif (cd_procedimento_p > 0) and (ie_origem_proc_p > 0) then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_proc_anest_w
	from 	proc_interno
	where	cd_procedimento = cd_procedimento_p
	and	ie_origem_proced = ie_origem_proc_p
	and	coalesce(ie_necessita_anest,'N') = 'S';

end if;

if (ie_proc_anest_w = 'N') and (nr_seq_ageint_p IS NOT NULL AND nr_seq_ageint_p::text <> '') then
	select	max(a.cd_pessoa_fisica),
			coalesce(max(ie_dependente),'N')
	into STRICT	cd_pessoa_Fisica_w,
			ie_dependente_w
	from	agenda_integrada a,
			pessoa_Fisica b
	where 	a.nr_Sequencia	= nr_seq_ageint_p
	and		a.cd_pessoa_fisica	= b.cd_pessoa_fisica;

	if (cd_pessoa_Fisica_w IS NOT NULL AND cd_pessoa_Fisica_w::text <> '') then

		qt_idade_w	:= obter_idade_pf(cd_pessoa_Fisica_w, clock_timestamp(), 'A');

		select	CASE WHEN count(*)=1 THEN 'S'  ELSE 'N' END
		into STRICT	ie_proc_anest_w
		from	regra_anest_proc_agenda
		where	nr_seq_proc_interno	= nr_seq_proc_interno_p
		and		ie_situacao	 = 'A'
		and		qt_idade_w between coalesce(qt_idade_min,0) and coalesce(qt_idade_max,999)
		and (ie_dependente_w	= coalesce(ie_dependente,'N') or ie_dependente = 'N');

	end if;
end if;

return	ie_proc_anest_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
