CREATE OR REPLACE FUNCTION ageint_obter_se_status_lib ( nr_seq_status_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_permite_w			varchar(1) := 'S';
qt_regra_existe_w		bigint;
qt_regra_existe_pf_w	bigint;


BEGIN

if (nr_seq_status_p IS NOT NULL AND nr_seq_status_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	count(*)
	into STRICT	qt_regra_existe_w
	from	ageint_regra_lib_status
	where	nr_seq_status = nr_seq_status_p;

	if (qt_regra_existe_w > 0) then
		select	count(*)
		into STRICT	qt_regra_existe_pf_w
		from	ageint_regra_lib_status
		where	nr_seq_status = nr_seq_status_p
		and		cd_pessoa_fisica = cd_pessoa_fisica_p;

		if (qt_regra_existe_pf_w = 0) then
			ie_permite_w	:= 'N';
		end if;
	else
		ie_permite_w	:= 'S';
	end if;

end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
