CREATE OR REPLACE FUNCTION ageint_obter_tipo_ag_exame ( nr_seq_proc_interno_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(15);


BEGIN

select	coalesce(max(b.ie_tipo_agendamento),'E')
into STRICT	ds_retorno_w
from	agenda_int_area a,
	agenda_int_grupo b,
	agenda_int_grupo_item c
where	b.nr_sequencia		= c.nr_seq_grupo
and	c.nr_seq_proc_interno	= nr_seq_proc_interno_p
and	b.ie_situacao		= 'A'
and	coalesce(a.cd_estabelecimento, cd_estabelecimento_p)	= cd_estabelecimento_p;

return	ds_retorno_w;

end;
