CREATE OR REPLACE FUNCTION ageint_obter_valores_itens_ad (nr_seq_item_p bigint, ie_tipo_valor_p text, nr_seq_item_Adic_p bigint default null) RETURNS bigint AS $body$
DECLARE


nr_valor_item_w		double precision := 0;
ie_suprimir_val_exames_w	varchar(1);


BEGIN

ie_suprimir_val_exames_w := obter_param_usuario(869, 360, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_suprimir_val_exames_w);

	if (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') and (ie_tipo_valor_p IS NOT NULL AND ie_tipo_valor_p::text <> '')then
		begin

		if (ie_suprimir_val_exames_w = 'S')then
			begin

			if (ie_tipo_valor_p = 'V')then

				select	max(0)
				into STRICT	nr_valor_item_w
				from	ageint_exame_adic_item
				where	nr_seq_item = nr_seq_item_p;
			else
				select	max(0)
				into STRICT	nr_valor_item_w
				from	ageint_exame_adic_item
				where	nr_seq_item = nr_seq_item_p;
			end if;

			end;
		else
			begin

			if (ie_tipo_valor_p = 'V')then

				select	max(vl_item)
				into STRICT	nr_valor_item_w
				from	ageint_exame_adic_item
				where	nr_seq_item 	= nr_seq_item_p
				and		nr_sequencia	= coalesce(nr_seq_item_Adic_p,nr_sequencia);
			else
				select	max(vl_item_particular)
				into STRICT	nr_valor_item_w
				from	ageint_exame_adic_item
				where	nr_seq_item = nr_seq_item_p
				and		nr_sequencia	= coalesce(nr_seq_item_Adic_p,nr_sequencia);
			end if;

			end;
		end if;
		end;
	end if;

return	nr_valor_item_w;

end;
$body$
