SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ageint_obter_valor_adic ( nr_seq_ageint_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
vl_adic_pacote_w	double precision;
vl_adic_item_w		double precision;
ie_suprimir_val_exames_w	varchar(1);


BEGIN

ie_suprimir_val_exames_w := Obter_param_usuario(869, 360, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, obter_estabelecimento_ativo, ie_suprimir_val_exames_w);

if (ie_suprimir_val_exames_w = 'N')then

	select 	sum(a.vl_item)
	into STRICT	vl_adic_item_w
	from 	ageint_itens_adicionais a,
			agenda_integrada_item b
	where 	b.nr_seq_agenda_int	= nr_seq_ageint_p
	and 	a.nr_seq_ageint_item 	= b.nr_sequencia;

	select 	sum(a.vl_item)
	into STRICT	vl_adic_pacote_w
	from 	ageint_itens_adicionais a,
			ageint_proc_pacote b
	where 	b.nr_seq_Ageint		= nr_seq_ageint_p
	and 	a.nr_seq_ageint_pacote 	= b.nr_sequencia;
else
	select 	sum(a.vl_item)
	into STRICT	vl_adic_item_w
	from 	ageint_itens_adicionais a,
			agenda_integrada_item b
	where 	b.nr_seq_agenda_int	= nr_seq_ageint_p
	and 	a.nr_seq_ageint_item 	= b.nr_sequencia
	and		b.ie_tipo_agendamento	<> 'E';

	select 	sum(a.vl_item)
	into STRICT	vl_adic_pacote_w
	from 	ageint_itens_adicionais a,
			ageint_proc_pacote b,
			agenda_integrada_item c
	where 	b.nr_seq_Ageint			= nr_seq_ageint_p
	and 	a.nr_seq_ageint_pacote 	= b.nr_sequencia
	and		c.nr_sequencia			= a.nr_seq_ageint_item;

end if;

vl_retorno_w	:= (coalesce(vl_adic_pacote_w,0) + coalesce(vl_adic_item_w,0));

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
