SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ageint_obter_valor_agendamento ( nr_seq_ageint_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


vl_item_w				double precision;
vl_exame_w				double precision := 0;
vl_matmed_w				double precision;
vl_pacote_w				double precision;
vl_total_w				double precision;
vl_lanc_auto_w			double precision;
vl_proc_adic_w  		double precision;
ie_tipo_agendamento_w	varchar(1);
nr_sequencia_w			bigint;

ie_calcula_lanc_auto_w		varchar(1);
ie_calcula_exame_adic_w 	varchar(1);
ie_considera_item_canc_w	varchar(1);
ie_suprimir_val_exames_w	varchar(1);
IE_CALCULA_GLOSA_w			parametro_Agenda_integrada.ie_calcula_glosa%type;


BEGIN

select	coalesce(max(IE_CALCULA_GLOSA),'N')
into STRICT	IE_CALCULA_GLOSA_w
from	parametro_Agenda_integrada
where	coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento)	= wheb_usuario_pck.get_cd_estabelecimento;

select	coalesce(max(Obter_Valor_Param_Usuario(869, 7, Obter_Perfil_Ativo, nm_usuario_p, 0)), 'S')
into STRICT	ie_calcula_lanc_auto_w
;

select  coalesce(max(Obter_Valor_Param_Usuario(869, 261, Obter_Perfil_Ativo, nm_usuario_p, 0)), 'N')
into STRICT	ie_calcula_exame_adic_w
;

ie_considera_item_canc_w := Obter_param_Usuario(869, 283, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_considera_item_canc_w);
ie_suprimir_val_exames_w := Obter_param_usuario(869, 360, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_suprimir_val_exames_w);

if (ie_suprimir_val_exames_w = 'N')then

	select	coalesce(sum(vl_item),0),
			coalesce(sum(vl_lanc_auto),0)
	into STRICT	vl_item_w,
			vl_lanc_auto_w
	from 	agenda_integrada_item
	where 	nr_seq_agenda_int = nr_seq_ageint_p
	and 	coalesce(nr_seq_proc_pacote::text, '') = ''
	and		((ie_considera_item_canc_w = 'S') or (SUBSTR(ageint_obter_status_item(nr_seq_agenda_int,nr_sequencia,'C'),1,255) <> 'C'));

	select	coalesce(sum(a.vl_exame),0)
	into STRICT	vl_exame_w
	from	ageint_exame_associado a,
			agenda_integrada_item b
	where	a.nr_seq_ageint_item	= b.nr_sequencia
	and		b.nr_seq_agenda_int 	= nr_seq_ageint_p
	and		((ie_considera_item_canc_w = 'S') or (SUBSTR(ageint_obter_status_item(b.nr_seq_agenda_int, b.nr_sequencia,'C'),1,255) <> 'C'))
	and		OBTER_CALC_ITEM_ASSOC_AGEINT(b.nr_seq_proc_interno) = 'S';

	select	coalesce(sum(vl_material + vl_medicamento),0)
	into STRICT	vl_matmed_w
	from 	agenda_integrada
	where 	nr_sequencia = nr_seq_ageint_p;

	select	coalesce(sum(vl_pacote),0)
	into STRICT	vl_pacote_w
	from 	ageint_proc_pacote
	where 	nr_seq_ageint = nr_seq_ageint_p;

	if (ie_calcula_lanc_auto_w  = 'S') then
		vl_total_w	:= vl_item_w + vl_exame_w + vl_matmed_w + vl_pacote_w;
	else
		vl_total_w	:= vl_item_w + vl_exame_w + vl_matmed_w + vl_pacote_w + vl_lanc_auto_w;
	end if;

	if (ie_calcula_exame_adic_w = 'S') then
		select	CASE WHEN IE_CALCULA_GLOSA_w='N' THEN  coalesce(sum(a.vl_custo_operacional + a.vl_anestesista + a.vl_medico + a.vl_auxiliares + a.vl_materiais ),0)  ELSE coalesce(sum(a.vl_item),0) END
		into STRICT    vl_proc_adic_w
		from 	ageint_exame_adic_item a,
				agenda_integrada_item  b
		where 	b.nr_sequencia = a.nr_seq_item
		and		b.nr_seq_agenda_int = nr_seq_ageint_p
		and		((ie_considera_item_canc_w = 'S') or (SUBSTR(ageint_obter_status_item(b.nr_seq_agenda_int, b.nr_sequencia,'C'),1,255) <> 'C'));

		vl_total_w := (vl_total_w + vl_proc_adic_w);
	end if;
else
	select	coalesce(sum(vl_item),0),
			coalesce(sum(vl_lanc_auto),0)
	into STRICT	vl_item_w,
			vl_lanc_auto_w
	from 	agenda_integrada_item
	where 	nr_seq_agenda_int = nr_seq_ageint_p
	and 	coalesce(nr_seq_proc_pacote::text, '') = ''
	and		((ie_considera_item_canc_w = 'S') or (SUBSTR(ageint_obter_status_item(nr_seq_agenda_int,nr_sequencia,'C'),1,255) <> 'C'))
	and		ie_tipo_agendamento	<> 'E';

	select	coalesce(sum(a.vl_exame),0)
	into STRICT	vl_exame_w
	from	ageint_exame_associado a,
			agenda_integrada_item b
	where	a.nr_seq_ageint_item	= b.nr_sequencia
	and		b.nr_seq_agenda_int 	= nr_seq_ageint_p
	and		((ie_considera_item_canc_w = 'S') or (SUBSTR(ageint_obter_status_item(b.nr_seq_agenda_int, b.nr_sequencia,'C'),1,255) <> 'C'))
	and		b.ie_tipo_agendamento	<> 'E'
	and		OBTER_CALC_ITEM_ASSOC_AGEINT(b.nr_seq_proc_interno) = 'S';

	select	coalesce(sum(vl_material + vl_medicamento),0)
	into STRICT	vl_matmed_w
	from 	agenda_integrada
	where 	nr_sequencia = nr_seq_ageint_p;

	select	coalesce(sum(vl_pacote),0)
	into STRICT	vl_pacote_w
	from 	ageint_proc_pacote
	where 	nr_seq_ageint = nr_seq_ageint_p;

	if (ie_calcula_lanc_auto_w  = 'S') then
		vl_total_w	:= vl_item_w + vl_exame_w + vl_matmed_w + vl_pacote_w;
	else
		vl_total_w	:= vl_item_w + vl_exame_w + vl_matmed_w + vl_pacote_w + vl_lanc_auto_w;
	end if;

	if (ie_calcula_exame_adic_w = 'S') then
		select	CASE WHEN IE_CALCULA_GLOSA_w='N' THEN  coalesce(sum(a.vl_custo_operacional + a.vl_anestesista + a.vl_medico + a.vl_auxiliares + a.vl_materiais ),0)  ELSE coalesce(sum(a.vl_item),0) END
		into STRICT    vl_proc_adic_w
		from 	ageint_exame_adic_item a,
				agenda_integrada_item  b
		where 	b.nr_sequencia = a.nr_seq_item
		and		b.nr_seq_agenda_int = nr_seq_ageint_p
		and		((ie_considera_item_canc_w = 'S') or (SUBSTR(ageint_obter_status_item(b.nr_seq_agenda_int, b.nr_sequencia,'C'),1,255) <> 'C'))
		and		b.ie_tipo_agendamento		<> 'E';

		vl_total_w := (vl_total_w + vl_proc_adic_w);
	end if;

end if;

return	vl_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
