CREATE OR REPLACE FUNCTION agenda_classif_liberada (nm_usuario_lib_p text, cd_perfil_p bigint, nr_seq_classif_agenda_p bigint, ie_classif_agenda_p text, ie_tipo_agenda_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'S';
qt_regra_w		bigint;


BEGIN

if (nr_seq_classif_agenda_p IS NOT NULL AND nr_seq_classif_agenda_p::text <> '') then

	select count(*)
	into STRICT	qt_regra_w
	from	agenda_classif_lib
	where	ie_situacao = 'A'
	and	nr_seq_classif_agenda = nr_seq_classif_agenda_p;
	if (qt_regra_w > 0) then
		select coalesce(max(ie_visivel),'N')
		into STRICT ds_retorno_w
		from agenda_classif_lib
		where ie_situacao = 'A'
		and ie_tipo_agenda = 'E'
		and coalesce(nm_usuario_lib, nm_usuario_lib_p) = nm_usuario_lib_p
		and nr_seq_classif_agenda = nr_seq_classif_agenda_p
		and coalesce(cd_perfil, cd_perfil_p) = cd_perfil_p;
	else
		ds_retorno_w := 'S';
	end if;

elsif (ie_classif_agenda_p IS NOT NULL AND ie_classif_agenda_p::text <> '') then


	select count(1)
	into STRICT	qt_regra_w
	from	agenda_classif_lib
	where	ie_situacao = 'A'
	and ie_classif_agenda = ie_classif_agenda_p  LIMIT 1;
	
	if (qt_regra_w > 0) then
		select coalesce(max(ie_visivel),'N')
		into STRICT ds_retorno_w
		from agenda_classif_lib
		where ie_situacao = 'A'
		and ie_tipo_agenda = 'A'
		and coalesce(nm_usuario_lib, nm_usuario_lib_p) = nm_usuario_lib_p
		and ie_classif_agenda = ie_classif_agenda_p
		and coalesce(cd_perfil, cd_perfil_p) = cd_perfil_p;
	else
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
