SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION agenda_color_mm_status (cd_agenda_p bigint, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_color_w		varchar(10) := '';
qt_all_w        bigint;
qt_avail_w      bigint;
qt_booked_w     bigint;
qt_holiday_w    bigint;


BEGIN

select   count(1)
into STRICT     qt_holiday_w
from     feriado 
where    cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento 
and      pkg_date_utils.start_of(dt_feriado, 'dd') = dt_agenda_p;

if (qt_holiday_w > 0) then
    ie_color_w := 'YELLOW';
    goto finalize;
end if;

select  count(1)
into STRICT    qt_all_w
from    agenda_consulta
where   cd_agenda = cd_agenda_p
and     pkg_date_utils.start_of(dt_agenda, 'dd') = dt_agenda_p;

if (qt_all_w = 0) then
    goto finalize;
end if;

select  count(1)
into STRICT    qt_avail_w
from    agenda_consulta
where   cd_agenda = cd_agenda_p
and     pkg_date_utils.start_of(dt_agenda, 'dd') = dt_agenda_p
and     ie_status_agenda = 'L';

if (qt_avail_w > 0) then
    ie_color_w := 'BLUE';
    goto finalize;
end if;

select  count(1)
into STRICT    qt_booked_w
from    agenda_consulta
where   cd_agenda = cd_agenda_p
and     pkg_date_utils.start_of(dt_agenda, 'dd') = dt_agenda_p
and     ie_status_agenda <> 'L';

if (qt_all_w = qt_booked_w and qt_avail_w = 0) then
    ie_color_w := 'RED';
end if;

<<finalize>>

return ie_color_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION agenda_color_mm_status (cd_agenda_p bigint, dt_agenda_p timestamp) FROM PUBLIC;

