SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION agenda_obter_cod_usuario_masc (cd_usuario_convenio_p text, cd_convenio_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(50);
cd_usuario_convenio_w	varchar(50);
cd_usuario_conv_w	varchar(50);
qt_digitos_masc_w	bigint;
y			bigint;
ds_formato_w		varchar(50);
ds_mascara_codigo_w	varchar(30);
BEGIN

select	max(ds_mascara_codigo)
into STRICT	ds_mascara_codigo_w
from	convenio
where	cd_convenio = cd_convenio_p;

if (coalesce(ds_mascara_codigo_w, 'X')			<> 'X') and (coalesce(cd_usuario_convenio_p, 'X')	<> 'X') then

	ds_formato_w			:= '';
	FOR 	i IN REVERSE length(ds_mascara_codigo_w)..1 LOOP
		ds_formato_w		:= ds_formato_w || substr(ds_mascara_codigo_w, i, 1);
	end loop;

	cd_usuario_convenio_w		:= '';
	FOR 	i IN REVERSE length(cd_usuario_convenio_p)..1 LOOP
		cd_usuario_convenio_w	:= cd_usuario_convenio_w || substr(cd_usuario_convenio_p, i, 1);
	end loop;

	qt_digitos_masc_w	:= length(ds_mascara_codigo_w);
	y	:= 1;

	FOR	x in 1..qt_digitos_masc_w loop
		if (substr(ds_formato_w, x, 1) = '0') or (substr(ds_formato_w, x, 1) = '9') or (substr(ds_formato_w, x, 1) = 'L') or (substr(ds_formato_w, x, 1) = 'I') or (substr(ds_formato_w, x, 1) = 'a') or (substr(ds_formato_w, x, 1) = 'A') then
			cd_usuario_conv_w	:= cd_usuario_conv_w || substr(cd_usuario_convenio_w, y, 1);
			y	:= y + 1;
		elsif (substr(ds_formato_w, x, 1) = 'X') then
			y	:= y + 1;
		else
			cd_usuario_conv_w	:= cd_usuario_conv_w || substr(ds_formato_w, x, 1);
		end if;
	end loop;

	FOR 	i IN REVERSE length(cd_usuario_conv_w)..1 LOOP
		ds_retorno_w	:= ds_retorno_w || substr(cd_usuario_conv_w, i, 1);
	end loop;

--	ds_retorno_w		:= cd_usuario_conv_w;
else
	ds_retorno_w	:= cd_usuario_convenio_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
