CREATE OR REPLACE FUNCTION agenda_obter_obs_padrao ( nr_seq_regra_p bigint, nr_seq_agenda_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


