CREATE OR REPLACE FUNCTION agenda_obter_tel_masc (nr_telefone_p text, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(50);
nr_telefone_w		varchar(50);
cd_usuario_conv_w	varchar(50);
qt_digitos_masc_w	bigint;
y			bigint;
ds_formato_w		varchar(50);
ds_mascara_telefone_w	varchar(30);
BEGIN

ds_mascara_telefone_w := obter_param_usuario(0, 51, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ds_mascara_telefone_w);

if (coalesce(ds_mascara_telefone_w, 'X')			<> 'X') and (coalesce(nr_telefone_p, 'X')	<> 'X') then

	ds_formato_w			:= '';
	FOR 	i IN 1..length(ds_mascara_telefone_w) LOOP
		ds_formato_w		:= ds_formato_w || substr(ds_mascara_telefone_w, i, 1);
	end loop;

	nr_telefone_w		:= '';
	FOR 	i IN 1..length(nr_telefone_p) LOOP
		nr_telefone_w	:= nr_telefone_w || substr(nr_telefone_p, i, 1);
	end loop;

	qt_digitos_masc_w	:= length(ds_mascara_telefone_w);
	y	:= 1;


	for	x in 1..qt_digitos_masc_w loop
		if (substr(ds_formato_w, x, 1) = '0') or (substr(ds_formato_w, x, 1) = '9') or (substr(ds_formato_w, x, 1) = 'L') or (substr(ds_formato_w, x, 1) = 'I') or (substr(ds_formato_w, x, 1) = 'a') or (substr(ds_formato_w, x, 1) = 'A') then
			cd_usuario_conv_w	:= cd_usuario_conv_w || substr(obter_somente_numero(nr_telefone_w), y, 1);
			y	:= y + 1;
		elsif (substr(ds_formato_w, x, 1) = 'X') then
			y	:= y + 1;
		else
			cd_usuario_conv_w	:= cd_usuario_conv_w || substr(ds_formato_w, x, 1);
		end if;
	end loop;

	FOR 	i IN 1..length(cd_usuario_conv_w) LOOP
		ds_retorno_w	:= ds_retorno_w || substr(cd_usuario_conv_w, i, 1);
	end loop;
else
	ds_retorno_w	:= nr_telefone_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
