CREATE OR REPLACE FUNCTION ageserv_obter_modelo_agend (nr_seq_rp_mod_item_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_modelo_w			varchar(255);
nr_seq_modelo_agendamento_w	bigint;
ds_retorno_w			varchar(255);


BEGIN


select	max(b.nr_seq_modelo_agendamento)
into STRICT	nr_seq_modelo_agendamento_w
from	rp_pac_modelo_agend_item a,
	rp_pac_modelo_agendamento b
where	a.nr_seq_modelo_pac = b.nr_sequencia
and	a.nr_sequencia = nr_seq_rp_mod_item_p;

select	max(ds_modelo)
into STRICT	ds_modelo_w
from	rp_modelo_agendamento
where	nr_sequencia = nr_seq_modelo_agendamento_w;

if (ie_opcao_p = 'C') then
	ds_retorno_w := nr_seq_modelo_agendamento_w;
elsif (ie_opcao_p = 'D') then
	ds_retorno_w := ds_modelo_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
