CREATE OR REPLACE FUNCTION ageserv_obter_se_atend_fechado (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
dt_alta_w	timestamp;
qt_contas_w	bigint;
qt_conta_atend_w	bigint;
ie_retorno_w	varchar(1) := 'N';			
			 

BEGIN 
 
select	max(dt_alta) 
into STRICT	dt_alta_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
select	count(*) 
into STRICT	qt_conta_atend_w 
from	conta_paciente 
where	nr_atendimento = nr_atendimento_p;
	 
if (qt_conta_atend_w > 0) then 
	select	count(*) 
	into STRICT	qt_contas_w 
	from	conta_paciente 
	where	nr_atendimento 	 = nr_atendimento_p 
	and	ie_status_acerto = 1;
 
	if (dt_alta_w IS NOT NULL AND dt_alta_w::text <> '') and (qt_contas_w = 0) then 
		ie_retorno_w	:= 'S';
	end if;
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
