SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ageserv_regra_status_alta (nr_atendimento_p bigint, ie_status_agenda_p text) RETURNS varchar AS $body$
DECLARE

 
qt_regra_w		bigint;
dt_alta_w		timestamp;
qt_contas_w		bigint;
ie_retorno_w		varchar(1) := 'N';
				

BEGIN 
 
if (nr_atendimento_p > 0) then 
	select	count(*) 
	into STRICT	qt_regra_w 
	from	agenda_status_alta 
	where	ie_status_agenda = ie_status_agenda_p;
 
	if (qt_regra_w > 0) then 
		select	max(dt_alta) 
		into STRICT	dt_alta_w 
		from	atendimento_paciente 
		where	nr_atendimento = nr_atendimento_p;
			 
		select	count(*) 
		into STRICT	qt_contas_w 
		from	conta_paciente 
		where	nr_atendimento 	 = nr_atendimento_p 
		and	ie_status_acerto = 1;
		 
		if (dt_alta_w IS NOT NULL AND dt_alta_w::text <> '') and (qt_contas_w = 0) then 
			ie_retorno_w	:= 'S';
		end if;
	end if;
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
