SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ageweb_ie_atualiza_dados (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


dt_atual_inf_pac_w	timestamp;
qt_dias_atual_inf_pac_w	bigint;
ie_retorno_w		varchar(1);


BEGIN

ie_retorno_w	:= 'N';

select	max(qt_dias_atual_inf_pac)
into STRICT	qt_dias_atual_inf_pac_w
from	parametro_agenda_web;

select	max(dt_atual_inf_pac)
into STRICT	dt_atual_inf_pac_w
from	ageweb_login
where	ds_login = cd_pessoa_fisica_p;

if (qt_dias_atual_inf_pac_w IS NOT NULL AND qt_dias_atual_inf_pac_w::text <> '') and (dt_atual_inf_pac_w IS NOT NULL AND dt_atual_inf_pac_w::text <> '') then

	if	((trunc(clock_timestamp()) - trunc(dt_atual_inf_pac_w)) >= qt_dias_atual_inf_pac_w) then
		ie_retorno_w	:= 'S';
	else
		ie_retorno_w	:= 'N';
	end if;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
