SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ageweb_obter_comprovante (nr_seq_agenda_p bigint, cd_tipo_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(4000);			
 
dt_agenda_w	agenda_consulta.dt_agenda%type;
nm_paciente_w	agenda_consulta.nm_paciente%type;
ds_classificacao_w	varchar(255);
nm_medico_w	pessoa_fisica.nm_pessoa_fisica%type;
ds_item_w	varchar(255);
nm_estab_fantasia_w	varchar(255);
			

BEGIN 
 
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then 
	if (cd_tipo_agenda_p = 3) then 
		select	max(a.dt_agenda), 
			max(a.nm_paciente), 
			max(substr(obter_classif_agenda_turno(a.ie_classif_agenda, null), 1, 200)), 
			max(substr(obter_nome_pf(b.cd_pessoa_fisica),1,255)), 
			max(substr(obter_nome_especialidade(b.cd_especialidade),1,255)), 
			max(substr(obter_nome_fantasia_estab(b.cd_estabelecimento),1,255)) 
		into STRICT	dt_agenda_w, 
			nm_paciente_w, 
			ds_classificacao_w, 
			nm_medico_w, 
			ds_item_w, 
			nm_estab_fantasia_w 
		from	agenda_consulta a, 
			agenda b 
		where	a.cd_agenda = b.cd_agenda 
		and	a.nr_sequencia = nr_seq_agenda_p;
	else 
		select	max(a.hr_inicio), 
			max(a.nm_paciente), 
			max(substr(obter_desc_classif_agenda_pac(nr_seq_classif_agenda),1,80)), 
			max(substr(obter_nome_pf(a.cd_medico_exec),1,255)), 
			max(substr(obter_exame_agenda(cd_procedimento, ie_origem_proced, nr_seq_proc_interno),1,240)), 
			max(substr(obter_nome_fantasia_estab(b.cd_estabelecimento),1,255)) 
		into STRICT	dt_agenda_w, 
			nm_paciente_w, 
			ds_classificacao_w, 
			nm_medico_w, 
			ds_item_w, 
			nm_estab_fantasia_w 
		from	agenda_paciente a, 
			agenda b 
		where	a.cd_agenda = b.cd_agenda 
		and	a.nr_sequencia = nr_seq_agenda_p;
	end if;
		 
	select	max(ds_corpo_comp) 
	into STRICT	ds_retorno_w 
	from	ageweb_dados_comprovante;
 
	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then 
		ds_retorno_w	:= substr(replace_macro(ds_retorno_w, '@paciente', nm_paciente_w),1,4000);
		ds_retorno_w	:= substr(replace_macro(ds_retorno_w, '@data', to_char(dt_agenda_w,'dd/mm/yyyy hh24:mi')),1,4000);
		ds_retorno_w	:= substr(replace_macro(ds_retorno_w, '@agendamento', ds_item_w),1,4000);
		ds_retorno_w	:= substr(replace_macro(ds_retorno_w, '@estab', nm_estab_fantasia_w),1,4000);
		ds_retorno_w	:= substr(replace_macro(ds_retorno_w, '@especialista', nm_medico_w),1,4000);
		ds_retorno_w	:= substr(replace_macro(ds_retorno_w, '@classif', ds_classificacao_w),1,4000);
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
