CREATE OR REPLACE FUNCTION ageweb_obter_se_permite_canc (dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_permite_w		varchar(1) := 'S';
qt_min_cancelamento_w	bigint;


BEGIN

select	coalesce(max(qt_min_cancelamento),0)
into STRICT	qt_min_cancelamento_w
from	parametro_agenda_web;

if	((dt_agenda_p - qt_min_cancelamento_w/1440) < clock_timestamp()) then
	ie_permite_w := 'N';
end if;

return	ie_permite_w;

end;
