CREATE OR REPLACE FUNCTION ageweb_obter_vl_param (ds_atributo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
ie_utiliza_exames_lab_w		varchar(1);
ie_pesquisa_especialidade_w	varchar(1);
ie_obriga_motivo_canc_w		varchar(1);
ie_utiliza_calendario_w		varchar(1);
nr_dias_perm_calendario_w	bigint;
ie_utiliza_dependente_w		varchar(1);
ie_permite_cancelar_w		varchar(1);


BEGIN

select 	max(ie_utiliza_exames_lab),
	max(ie_pesquisa_especialidade),
	max(ie_obriga_motivo_canc),
	max(ie_utiliza_calendario),
	max(nr_dias_perm_calendario),
	max(ie_utiliza_dependente),
	max(ie_permite_cancelar)
into STRICT	ie_utiliza_exames_lab_w,
	ie_pesquisa_especialidade_w,
	ie_obriga_motivo_canc_w,
	ie_utiliza_calendario_w,
	nr_dias_perm_calendario_w,
	ie_utiliza_dependente_w,
	ie_permite_cancelar_w
from	parametro_agenda_web;

if (ds_atributo_p = 'IE_UTILIZA_EXAMES_LAB') then
	ds_retorno_w := ie_utiliza_exames_lab_w;
elsif (ds_atributo_p = 'IE_PESQUISA_ESPECIALIDADE') then
	ds_retorno_w := ie_pesquisa_especialidade_w;
elsif (ds_atributo_p = 'IE_OBRIGA_MOTIVO_CANC') then
	ds_retorno_w := ie_obriga_motivo_canc_w;
elsif (ds_atributo_p = 'IE_UTILIZA_CALENDARIO') then
	ds_retorno_w := ie_utiliza_calendario_w;
elsif (ds_atributo_p = 'NR_DIAS_PERM_CALENDARIO') then
	ds_retorno_w := nr_dias_perm_calendario_w;
elsif (ds_atributo_p = 'IE_UTILIZA_DEPENDENTE') then
	ds_retorno_w :=  ie_utiliza_dependente_w;
elsif (ds_atributo_p = 'IE_PERMITE_CANCELAR') then
	ds_retorno_w :=  ie_permite_cancelar_w;
end if;

return	ds_retorno_w;

end;
