CREATE OR REPLACE FUNCTION age_obter_se_segurado_ativo (nr_seq_segurado_p bigint, dt_agendamento_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);

dt_liberacao_w		timestamp;
dt_migracao_w		timestamp;
dt_rescisao_w		timestamp;
dt_limite_utilizacao_w	timestamp;
dt_cancelamento_w	timestamp;


BEGIN

select	max(dt_liberacao),
	max(dt_migracao),
	max(dt_rescisao),
	max(dt_limite_utilizacao),
	max(dt_cancelamento)
into STRICT	dt_liberacao_w,
	dt_migracao_w,
	dt_rescisao_w,
	dt_limite_utilizacao_w,
	dt_cancelamento_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') and
	(((coalesce(dt_rescisao_w::text, '') = '') and (coalesce(dt_cancelamento_w::text, '') = '')) or ((dt_rescisao_w IS NOT NULL AND dt_rescisao_w::text <> '') and (coalesce(dt_limite_utilizacao_w,dt_rescisao_w) >= dt_agendamento_p))) then

	ds_retorno_w := 'A';

elsif	((dt_rescisao_w IS NOT NULL AND dt_rescisao_w::text <> '') or (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '')) then

	ds_retorno_w := 'I';

elsif (coalesce(dt_liberacao_w::text, '') = '') and (dt_migracao_w IS NOT NULL AND dt_migracao_w::text <> '') and (coalesce(dt_rescisao_w::text, '') = '') and (coalesce(dt_cancelamento_w::text, '') = '') then

	ds_retorno_w := 'M';

elsif (coalesce(dt_liberacao_w::text, '') = '') and (coalesce(dt_migracao_w::text, '') = '') then

	ds_retorno_w := 'D';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
