CREATE OR REPLACE FUNCTION aghos_obter_se_nova_evol (nr_seq_solicitacao_p bigint) RETURNS varchar AS $body$
DECLARE


dt_visualizacao_evol_w	timestamp;
ie_nova_evol_w		varchar(1);


BEGIN

select	max(dt_visualizacao_evol)
into STRICT	dt_visualizacao_evol_w
from	solicitacao_tasy_aghos
where	nr_sequencia = nr_seq_solicitacao_p;

begin
	select	'S'
	into STRICT	ie_nova_evol_w
	from	evolucao_tasy_aghos
	where	dt_atualizacao_nrec > coalesce(dt_visualizacao_evol_w, clock_timestamp() - interval '1000 days')
	and	nr_seq_solicitacao = nr_seq_solicitacao_p  LIMIT 1;
exception
	when 	others then
		ie_nova_evol_w := 'N';
end;

return	ie_nova_evol_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
