CREATE OR REPLACE FUNCTION agiq_obter_resposta_grid ( nr_seq_ageint_resp_quest_p bigint, nr_seq_estrutura_p bigint, nr_seq_resp_p bigint, ds_resposta_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
nr_seq_estrutura_w	ageint_resp_quest.nr_seq_estrutura%type;
ds_resposta_w		ageint_resp_quest.ds_resposta%type;
nr_seq_resp_w		ageint_resp_quest.nr_seq_resp%type;
ie_tipo_resposta_w	ageint_quest_estrutura.ie_tipo_resposta%type;


BEGIN

if	((coalesce(nr_seq_estrutura_p::text, '') = '') and (coalesce(ds_resposta_p::text, '') = '') and (coalesce(nr_seq_resp_p::text, '') = '') and (nr_seq_ageint_resp_quest_p IS NOT NULL AND nr_seq_ageint_resp_quest_p::text <> '')) then

	select	max(r.nr_seq_estrutura),
		max(r.nr_seq_resp),
		max(r.ds_resposta),
		max(e.ie_tipo_resposta)
	into STRICT	nr_seq_estrutura_w,
		nr_seq_resp_w,
		ds_resposta_w,
		ie_tipo_resposta_w
	from	ageint_resp_quest r,
		ageint_quest_estrutura e
	where	e.nr_sequencia = r.nr_seq_estrutura
	and	r.nr_sequencia = nr_seq_ageint_resp_quest_p;

end if;

if (coalesce(ie_tipo_resposta_w::text, '') = '') then
	ie_tipo_resposta_w	:= obter_tipo_resp_estrut_agiq(nr_seq_estrutura_p);
end if;

if (coalesce(nr_seq_resp_w::text, '') = '') then
	nr_seq_resp_w	:= nr_seq_resp_p;
end if;

if (coalesce(ds_resposta_w::text, '') = '') then
	ds_resposta_w	:= ds_resposta_p;
end if;

if (ie_tipo_resposta_w = 'G') then
	select	max(a.ds_opcao)
	into STRICT	ds_retorno_w
	from	ageint_quest_grupo_opcao a
	where	a.nr_sequencia = nr_seq_resp_w;
else
	ds_retorno_w	:=	ds_resposta_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
