SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION agserv_atualiza_seq_lista_esp (cd_pessoa_fisica_p text, nr_sequencia_p text) RETURNS varchar AS $body$
DECLARE

 
nr_seq_lista_espera_w     	bigint;
cd_agenda_w			bigint;
ds_agenda_w			varchar(200);
ds_nome_agenda_w		varchar(50);
ds_retorno_w			varchar(10);


BEGIN 
 
SELECT 	coalesce(MAX(a.cd_agenda),0), 
	MAX(SUBSTR(obter_nome_medico(b.cd_pessoa_fisica,'N'),1,200)), 
	coalesce(MAX(b.ds_agenda),''), 
	MAX(a.nr_sequencia) 
INTO STRICT	cd_agenda_w, 
	ds_agenda_w, 
	ds_nome_agenda_w, 
	nr_seq_lista_espera_w 
FROM 	agenda_lista_espera a, 
	agenda b 
WHERE	a.cd_agenda = b.cd_agenda 
AND	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
AND 	ie_status_espera = 'A' 
AND	coalesce(b.ie_lista_espera,'N') = 'N';
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (cd_agenda_w IS NOT NULL AND cd_agenda_w::text <> '')	and (ds_agenda_w IS NOT NULL AND ds_agenda_w::text <> '')	or (ds_nome_agenda_w IS NOT NULL AND ds_nome_agenda_w::text <> '')	then 
		 
	ds_retorno_w	:= nr_seq_lista_espera_w;
END IF;
 
RETURN	ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
