CREATE OR REPLACE FUNCTION agserv_obter_med_solic (cd_pessoa_fisica_p text, dt_atual_p timestamp) RETURNS varchar AS $body$
DECLARE


cd_medico_solic_w		varchar(255);


BEGIN
IF (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (dt_atual_p IS NOT NULL AND dt_atual_p::text <> '') THEN
	BEGIN
	SELECT	MAX(a.cd_medico_solic)
	into STRICT	cd_medico_solic_w
	FROM	agenda_consulta a
	WHERE	a.nr_sequencia = ((	SELECT MAX(a.nr_sequencia)
					FROM	 agenda_consulta a
					WHERE	 a.cd_pessoa_fisica = cd_pessoa_fisica_p
					AND	 a.dt_agenda <= dt_atual_p
					AND	 (a.cd_medico_solic IS NOT NULL AND a.cd_medico_solic::text <> '')
					AND	 a.ie_status_agenda NOT IN ('C','L','B')));

	END;
END IF;

RETURN cd_medico_solic_w;
END;
