SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION aih_consistir_mudanca_proc ( nr_aih_p bigint) RETURNS varchar AS $body$
DECLARE


cd_proc_solic_w		bigint;
ie_laudo_mudanca_w	smallint;
cd_inconsistencia_w	varchar(6) := '';


BEGIN

if (coalesce(nr_aih_p,0) > 0) then
	select	max(cd_procedimento_solic)
	into STRICT	cd_proc_solic_w
	from	sus_laudo_paciente
	where	nr_aih = nr_aih_p
	  and	ie_tipo_laudo_sus = 0;

	if (AIH_Validar_Regra(2, cd_proc_solic_w) > 0) then
		select	count(*)
		into STRICT	ie_laudo_mudanca_w
		from sus_laudo_paciente
		where	nr_aih = nr_aih_p
		  and	ie_tipo_laudo_sus = 1;

		if (ie_laudo_mudanca_w > 0) then
			select	to_char(b.cd_inconsistencia) || ' '
			into STRICT	cd_inconsistencia_w
			from	inconsistencia b,
				aih_regra a
			where	a.nr_seq_inconsistencia = b.nr_sequencia
			  and	a.nr_sequencia		= 2;
		end if;
	end if;
end if;

return cd_inconsistencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
