CREATE OR REPLACE FUNCTION ajustar_abas_import_migr (nm_obj_aba_p text) RETURNS varchar AS $body$
DECLARE


nm_obj_aba_w		varchar(50);
nr_pos_underscore_w	smallint;
nm_obj_aba_ww		varchar(50);


BEGIN
if (nm_obj_aba_p IS NOT NULL AND nm_obj_aba_p::text <> '') then
	begin
	nm_obj_aba_w		:= lower(nm_obj_aba_p);
	nm_obj_aba_w 		:= replace(nm_obj_aba_w,'_ts','');
	nr_pos_underscore_w	:= position('_' in nm_obj_aba_w);

	while(nr_pos_underscore_w > 0) loop
		begin
		nm_obj_aba_ww		:= substr(nm_obj_aba_w,1,nr_pos_underscore_w - 1);
		nm_obj_aba_ww		:= nm_obj_aba_ww || initcap(substr(nm_obj_aba_w,nr_pos_underscore_w + 1,length(nm_obj_aba_w)));
		nm_obj_aba_w		:= nm_obj_aba_ww;
		nr_pos_underscore_w	:= position('_' in nm_obj_aba_w);
		end;
	end loop;

	nm_obj_aba_w := obter_initcap_classe_java(nm_obj_aba_w);
	nm_obj_aba_w := nm_obj_aba_w || 'WJP';
	end;
end if;
return nm_obj_aba_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
