CREATE OR REPLACE FUNCTION ajustar_dosagem_sol ( qt_dosagem_p bigint) RETURNS varchar AS $body$
DECLARE


qt_dosagem_w	varchar(50);


BEGIN

if (qt_dosagem_p >= 1) then
	qt_dosagem_w	:= to_char(qt_dosagem_p);
elsif (substr(qt_dosagem_p,1,1) = ',') then
	qt_dosagem_w	:= '0' || to_char(qt_dosagem_p);
end if;
qt_dosagem_w	:= replace(qt_dosagem_w,'.',',');

return	qt_dosagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
