CREATE OR REPLACE FUNCTION ajustar_valor_numerico (qt_valor_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (substr(trim(both qt_valor_p),0,1) = ',' ) then

	ds_retorno_w := '0' || trim(both qt_valor_p);
end if;

if ( substr(trim(both qt_valor_p),0,1) <> ',') then
	ds_retorno_w := qt_valor_p;
end if;

return	ds_retorno_w;

end;
$body$
