CREATE OR REPLACE FUNCTION allow_generate_assoc_amount ( nr_atendimento_p bigint, nr_seq_assoc_proc_p bigint, qt_max_prescricao bigint ) RETURNS varchar AS $body$
DECLARE

    ie_return_w            varchar(1) := 'S';
    qt_proc_prescribed_w   bigint := 0;

BEGIN
    IF ( (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') AND coalesce(nr_seq_assoc_proc_p, 0) > 0 AND coalesce(qt_max_prescricao, 0) > 0 ) THEN
        SELECT
            COUNT(1)
        INTO STRICT qt_proc_prescribed_w
        FROM
            cpoe_procedimento c
        WHERE
            coalesce(c.dt_lib_suspensao::text, '') = ''
            AND coalesce(c.dt_suspensao::text, '') = ''
            AND (c.DT_LIBERACAO IS NOT NULL AND c.DT_LIBERACAO::text <> '')
            AND c.nr_atendimento = nr_atendimento_p
            AND c.nr_seq_proc_interno = nr_seq_assoc_proc_p;

        IF ( qt_proc_prescribed_w = qt_max_prescricao ) THEN
            ie_return_w := 'N';
        END IF;
    END IF;

    RETURN ie_return_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
