CREATE OR REPLACE FUNCTION allow_update_ehro_item (nr_cirurgia_p bigint, nr_seq_pepo_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_permite_alterar_w 		varchar(1) := 'S';
ie_perm_real_w				varchar(1)	:= '';
ie_permite_inserir_info_w	varchar(1)	:= '';
qt_min_apos_alta_w			bigint	:= 0;
dt_atendimento_w			timestamp		:= null;


BEGIN 
 
ie_perm_real_w 				:= obter_valor_param_usuario(872, 119, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento);
ie_permite_inserir_info_w	:= obter_valor_param_usuario(872, 90, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento);
qt_min_apos_alta_w			:= (coalesce(obter_valor_param_usuario(872, 131, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento), 0))::numeric;
 
if (ie_perm_real_w = 'N') then 
	if (coalesce(nr_cirurgia_p,0) > 0) then 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_permite_alterar_w 
		from	cirurgia 
		where	nr_cirurgia = nr_cirurgia_p 
		and		ie_status_cirurgia not in (3,4);
	else	 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_permite_alterar_w 
		from	cirurgia 
		where	nr_seq_pepo = nr_seq_pepo_p 
		and		ie_status_cirurgia in (3,4);
	end if;
end if;	
	 
if (ie_permite_inserir_info_w = 'N' and ie_permite_alterar_w = 'S') then 
	begin 
	dt_atendimento_w := TO_DATE(obter_dados_atendimento(nr_atendimento_p, 'DA'), 'dd/mm/yyyy hh24:mi:ss');
	if (dt_atendimento_w IS NOT NULL AND dt_atendimento_w::text <> '') and 
		(clock_timestamp() >= (dt_atendimento_w + (qt_min_apos_alta_w/1440))) then 
		begin 
			ie_permite_alterar_w := 'N';
		end;
	end if;
	end;
end if;						
 
return ie_permite_alterar_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
