CREATE OR REPLACE FUNCTION ama_consiste_atend ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

cd_pessoa_fisica_w	varchar(10);
qt_laudo_apac_w		smallint	:= 0;
ie_result_w		varchar(2)	:= 'N';
nr_atendimento_w	bigint;
ie_clinica_w		integer;
qt_proc_consulta_w	integer	:= 0;

BEGIN
select	count(*) 
into STRICT	qt_proc_consulta_w 
from	atend_medico_ambulatorial a, 
	proced_medico_ambulatorial b 
where	a.nr_sequencia		= b.nr_seq_ama 
and	a.nr_atendimento	= nr_atendimento_p 
and	b.cd_procedimento	= 07012047 
and	b.ie_origem_proced	= 3;
if (qt_proc_consulta_w <> 0) then 
	select	max(b.cd_pessoa_fisica), 
		max(b.ie_clinica) 
	into STRICT	cd_pessoa_fisica_w, 
		ie_clinica_w 
	from	atend_medico_ambulatorial a, 
		atendimento_paciente b 
	where	a.nr_atendimento	= b.nr_atendimento 
	and	a.nr_atendimento	= nr_atendimento_p;	
	begin 
	select	count(*) 
	into STRICT	qt_laudo_apac_w 
	from	sus_laudo_paciente a, 
		atendimento_paciente b, 
		procedimento_paciente c 
	where	a.ie_classificacao in (11,12,13) 
	and	a.ie_tipo_laudo_apac in (1,2,4) 
	and	b.cd_pessoa_fisica	= cd_pessoa_fisica_w 
	and	clock_timestamp() between a.dt_emissao and PKG_DATE_UTILS.ADD_MONTH(a.dt_emissao, 2, 0) 
	and	a.nr_atendimento 	= b.nr_atendimento 
	and	c.nr_atendimento 	= b.nr_atendimento 
	and	b.ie_clinica		= ie_clinica_w 
	and	a.nr_atendimento	<> nr_atendimento_p 
	and	c.cd_procedimento	= 07012047 
	and	c.ie_origem_proced	= 3;
	exception 
	when others then 
		qt_laudo_apac_w	:= 0;
	end;
	if (qt_laudo_apac_w > 0) then 
		ie_result_w	:= 'S';
	end if;		
end if;
return ie_result_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION ama_consiste_atend ( nr_atendimento_p bigint) FROM PUBLIC;

