CREATE OR REPLACE FUNCTION ama_consiste_proced_espec ( nr_sequencia_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE

cd_pessoa_fisica_w	varchar(10);
qt_laudo_apac_w		smallint	:= 0;
ie_result_w		varchar(2)	:= 'N';
nr_atendimento_w	bigint;
ie_clinica_w		integer;


BEGIN 
/*verifica se o procedimento é consulta a ser testada*/
 
if (cd_procedimento_p	= 07012047) and (ie_origem_proced_p	= 3) then 
	/*atendimento, código pessoa, e clinica do atendimento da ama*/
 
	select	a.nr_atendimento, 
		b.cd_pessoa_fisica, 
		b.ie_clinica 
	into STRICT	nr_atendimento_w, 
		cd_pessoa_fisica_w, 
		ie_clinica_w 
	from	atend_medico_ambulatorial a, 
		atendimento_paciente b 
	where	a.nr_atendimento	= b.nr_atendimento 
	and	a.nr_sequencia		= nr_sequencia_p;	
	/*verifica se há laudo vigente e pega atendimento do laudo vigente*/
 
	begin 
	select	count(*) 
	into STRICT	qt_laudo_apac_w 
	from	sus_laudo_paciente a, 
		atendimento_paciente b, 
		procedimento_paciente c 
	where	a.ie_classificacao in (11,12,13) 
	and	a.ie_tipo_laudo_apac in (1,2,4) 
	and	b.cd_pessoa_fisica	= cd_pessoa_fisica_w 
	and	clock_timestamp() between a.dt_emissao and PKG_DATE_UTILS.ADD_MONTH(a.dt_emissao, 2, 0)	 
	and	a.nr_atendimento 	= b.nr_atendimento 
	and	c.nr_atendimento 	= b.nr_atendimento 
	and	b.ie_clinica		= ie_clinica_w 
	and	a.nr_atendimento	<> nr_atendimento_w 
	and	c.cd_procedimento	= cd_procedimento_p 
	and	c.ie_origem_proced	= ie_origem_proced_p;
	exception 
	when others then 
		qt_laudo_apac_w	:= 0;
	end;
	 
	if (qt_laudo_apac_w > 0) then 
		ie_result_w	:= 'S';
	end if;		
end if;
return ie_result_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
