SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION apac_obter_diretor_ups ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
cd_proc_principal_w		bigint;
ie_origem_proc_princ_w		bigint;
cd_estabelecimento_W		smallint;
cd_grupo_w			bigint;
cd_especialidade_w		bigint;
cd_area_w			bigint;

cd_pessoa_fisica_w		varchar(10);
nm_pessoa_fisica_w		varchar(60);
cd_cns_diretor_w		bigint;

ds_retorno_w			varchar(60);

C01 CURSOR FOR 
	SELECT	cd_pessoa_fisica, 
		substr(obter_nome_pf(cd_pessoa_fisica),1,60), 
		cd_cns_diretor 
	from	sus_apac_diretor_proc 
	where	coalesce(cd_procedimento,cd_proc_principal_w)	= cd_proc_principal_w 
	and	coalesce(ie_origem_proced,ie_origem_proc_princ_w)	= ie_origem_proc_princ_w 
	and	coalesce(cd_area_procedimento,cd_area_w)		= cd_area_w	 
	and	coalesce(cd_especialidade,cd_especialidade_w)	= cd_especialidade_w 
	and	coalesce(cd_grupo_proc,cd_grupo_w)			= cd_grupo_w 
	order by 
		coalesce(cd_procedimento,0), 
		coalesce(cd_grupo_proc,0), 
		coalesce(cd_especialidade,0), 
		coalesce(cd_area_procedimento,0);


BEGIN 
 
select	a.cd_proc_principal, 
	a.ie_origem_proc_princ, 
	b.cd_estabelecimento 
into STRICT	cd_proc_principal_w, 
	ie_origem_proc_princ_w, 
	cd_estabelecimento_W 
from	atendimento_paciente b, 
	sus_apac_movto a 
where	a.nr_atendimento	= b.nr_atendimento 
and	a.nr_sequencia		= nr_sequencia_p;
 
select	coalesce(max(cd_grupo_proc),0), 
	coalesce(max(cd_especialidade),0), 
	coalesce(max(cd_area_procedimento),0) 
into STRICT	cd_grupo_w, 
	cd_especialidade_w, 
	cd_area_w 
from	Estrutura_Procedimento_V 
where	cd_procedimento 	= cd_proc_principal_w 
and	ie_origem_proced	= ie_origem_proc_princ_w;
 
OPEN C01;
LOOP 
FETCH C01 into 
	cd_pessoa_fisica_w, 
	nm_pessoa_fisica_w, 
	cd_cns_diretor_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	cd_pessoa_fisica_w	:= cd_pessoa_fisica_w;
	cd_cns_diretor_w	:= cd_cns_diretor_w;
	nm_pessoa_fisica_w	:= nm_pessoa_fisica_w;
	end;
END LOOP;
CLOSE C01;
 
if (coalesce(cd_pessoa_fisica_w::text, '') = '') then 
	select	max(cd_diretor_ups), 
		substr(obter_nome_pf(max(cd_diretor_ups)),1,60), 
		max(cd_cns_diretor) 
	into STRICT	cd_pessoa_fisica_w, 
		nm_pessoa_fisica_w, 
		cd_cns_diretor_w 
	from	sus_parametros 
	where	cd_estabelecimento	= cd_estabelecimento_W;
end if;
 
if (ie_opcao_p = 'N') then 
	ds_retorno_w	:= nm_pessoa_fisica_w;
elsif (ie_opcao_p = 'C') then 
	ds_retorno_w	:= cd_pessoa_fisica_w;
elsif (ie_opcao_p = 'CNS') then 
	ds_retorno_w	:= cd_cns_diretor_w;
end if;
 
return ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
